profilesHolder.calculation:
formData ?
  periodTotal(get(formData, path('profilesValidator')), ['total'], 'profiles', 'instalment1', 'instalment2', 'instalment3', 'instalment4'):
  null;

setCreate(formData, path('profilesValidator', '_valid'),
  parseMoney(
    accumulateMoney(
      get(formData, path('profilesValidator', 'profiles')),
      'total',
      2
    )
  ) === parseMoney(
    get(formData, path('hifFundingAmount'))
  )
);
